import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, useNavigate } from 'react-router-dom';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import Navbar from './components/Navbar/Navbar';
import SearchAndSort from './components/SearchAndSort/SearchAndSort';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import JobListing from './components/JobListing/JobListing';
import AddJobDetails from '../src/components/AddJobDescription/'; // Add Job Page

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Check if user is logged in when the app loads
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  return (
    <Router>
      <>
        <ToastContainer />

        <Routes>
          {/* Navbar will only be included if it's not an excluded page */}
          <Route path="*" element={<ConditionalNavbar isLoggedIn={isLoggedIn} setIsLoggedIn={setIsLoggedIn} />} />
        </Routes>

        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/add-job" element={<AddJobDetails />} /> {/* Add Job Page */}
        </Routes>

        <SearchAndSort isLoggedIn={isLoggedIn} />
        <JobListing />
      </>
    </Router>
  );
}

// ðŸ›  Fix: Now inside `Routes` so `useLocation()` works correctly
const ConditionalNavbar = ({ isLoggedIn, setIsLoggedIn }) => {
  const location = useLocation();

  // Hide Navbar on Login, Register, and Add Job pages
  if (["/login", "/register", "/add-job"].includes(location.pathname)) {
    return null;
  }

  return <Navbar isLoggedIn={isLoggedIn} setIsLoggedIn={setIsLoggedIn} />;
};

export default App;
