import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate instead of useHistory
import "./Navbar.css";

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate(); // useNavigate hook for navigation

  // Check if the user is logged in when the component mounts
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  // Handle login redirect
  const handleLoginRedirect = () => {
    navigate("/login");
  };

  // Handle logout
  const handleLogout = () => {
    // Remove token from localStorage to log out the user
    localStorage.removeItem("token");
    setIsLoggedIn(false); // Update state to reflect logged out status
    navigate("/login"); // Redirect to login page
  };

  // Handle dashboard redirect
  const handleDashboardRedirect = () => {
    navigate("/home"); // Redirect to dashboard or home page
  };

  return (
    <nav className="navbar">
      <h1 className="logo">JobStation</h1>
      <div className="nav-buttons">
        {isLoggedIn ? (
          <>
            <button className="btn" onClick={handleLogout}>Logout</button>
            <button className="btn" onClick={handleDashboardRedirect}>Dashboard</button>
            <img className="profile-icon" src="https://via.placeholder.com/40" alt="Profile" />
          </>
        ) : (
          <>
            <button className="btn" onClick={handleLoginRedirect}>Login</button>
            <button className="btn" onClick={() => navigate("/register")}>Register</button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
