import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { useState, useEffect } from 'react';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import Navbar from './components/Navbar/Navbar';  // Correct import path for Navbar component
import SearchAndSort from './components/SearchAndSort/SearchAndSort';  // Correct import path for SearchAndSort component

import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Check if the user is logged in when the app starts (to persist login status)
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem('token');
  };

  return (
    <Router>
      <ToastContainer />  {/* âœ… Moved outside Routes */}

      {/* Passing isLoggedIn state to Navbar */}
      <Navbar isLoggedIn={isLoggedIn} setIsLoggedIn={setIsLoggedIn} />

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login setIsLoggedIn={handleLogin} />} />
        <Route path="/register" element={<Register />} />
      </Routes>

      {/* Pass isLoggedIn to SearchAndSort */}
      <SearchAndSort isLoggedIn={isLoggedIn} />
    </Router>
  );
}

export default App;
