import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./SearchAndSort.css";

const skillsOptions = ["HTML", "CSS", "SQLite", "Python", "JavaScript", "Node Js", "React Js", "Express Js"];
const jobTypes = ["Full-time", "Part-time", "Remote"];
const sortingOptions = ["Relevance", "Salary High to Low", "Salary Low to High", "Date Posted"];

const SearchAndSort = ({ isLoggedIn, onFilter }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedSkills, setSelectedSkills] = useState([]);
  const [selectedJobType, setSelectedJobType] = useState("");
  const [selectedLocation, setSelectedLocation] = useState("");
  const [selectedSalaryRange, setSelectedSalaryRange] = useState("");
  const [selectedSort, setSelectedSort] = useState("Relevance");

  const navigate = useNavigate();

  const handleSearchChange = (e) => setSearchTerm(e.target.value);
  const handleSkillChange = (e) => {
    const skill = e.target.value;
    if (skill && !selectedSkills.includes(skill)) {
      setSelectedSkills([...selectedSkills, skill]);
    }
    e.target.value = "";
  };
  const removeSkill = (index) => setSelectedSkills(selectedSkills.filter((_, i) => i !== index));
  const clearFilters = () => {
    setSearchTerm("");
    setSelectedSkills([]);
    setSelectedJobType("");
    setSelectedLocation("");
    setSelectedSalaryRange("");
    setSelectedSort("Relevance");
  };

  const handleApplyFilters = () => {
    onFilter({
      searchTerm,
      selectedSkills,
      selectedJobType,
      selectedLocation,
      selectedSalaryRange,
      selectedSort,
    });
  };

  return (
    <div className="search-filter-container">
      <input
        type="text"
        placeholder="Search jobs by title, company, or keywords..."
        value={searchTerm}
        onChange={handleSearchChange}
        className="search-input"
      />

      <div className="filter-row">
        <select onChange={handleSkillChange} className="skills-dropdown">
          <option value="">Select Skills</option>
          {skillsOptions.map((skill, index) => (
            <option key={index} value={skill}>
              {skill}
            </option>
          ))}
        </select>

        <select value={selectedJobType} onChange={(e) => setSelectedJobType(e.target.value)}>
          <option value="">Select Job Type</option>
          {jobTypes.map((type, index) => (
            <option key={index} value={type}>{type}</option>
          ))}
        </select>

        <input
          type="text"
          placeholder="Enter Location"
          value={selectedLocation}
          onChange={(e) => setSelectedLocation(e.target.value)}
        />

        <input
          type="number"
          placeholder="Enter Max Salary"
          value={selectedSalaryRange}
          onChange={(e) => setSelectedSalaryRange(e.target.value)}
        />

        <select value={selectedSort} onChange={(e) => setSelectedSort(e.target.value)}>
          {sortingOptions.map((option, index) => (
            <option key={index} value={option}>{option}</option>
          ))}
        </select>

        <button className="apply-filter-btn" onClick={handleApplyFilters}>
          Apply Filter
        </button>
        <button className="clear-filters" onClick={clearFilters}>
          Clear Filters
        </button>
      </div>
    </div>
  );
};

export default SearchAndSort;
