import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const JobDetails = () => {
  const { id } = useParams(); // Get the job ID from the URL
  const [jobDetails, setJobDetails] = useState(null);

  useEffect(() => {
    const fetchJobDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/jobs/${id}`); // Fetch details using the job ID
        setJobDetails(response.data); // Store the job details
      } catch (error) {
        console.error("Error fetching job details:", error);
      }
    };

    fetchJobDetails();
  }, [id]);

  if (!jobDetails) return <p>Loading...</p>;

  return (
    <div>
      <h1>{jobDetails.jobPosition}</h1>
      <p>{jobDetails.companyName}</p>
      <p>{jobDetails.jobDescription}</p>
      <p>{jobDetails.skillsRequired}</p>
      {/* Render other job details */}
    </div>
  );
};

export default JobDetails;
