import React, { useState } from "react";
import "./AddJobDescription.css";
import JobListingimg from "../../../src/assets/job-listing2.jpg";

const skillsList = [
  "HTML", "CSS", "React.js", "JavaScript", "Node.js", "MySQL", "Express.js",
  "MongoDB", "TypeScript", "Redux", "GraphQL", "Bootstrap", "Tailwind CSS",
  "Material UI", "Git", "GitHub", "Docker", "AWS", "Firebase", "PostgreSQL",
  "Python", "Django", "Flask", "PHP", "Laravel", "C++", "C#", "Java", "Spring Boot"
];

const AddJobDescription = () => {
  const [formData, setFormData] = useState({
    companyName: "",
    companyLogo: "",
    jobPosition: "",
    monthlySalary: "",
    jobType: "",
    remote_office: "",
    location: "",
    jobDescription: "",
    aboutCompany: "",
    skillsRequired: [],
    additionalInfo: "",
    hiringCount: "",
  });

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle multiple skills selection
  const handleSkillsChange = (e) => {
    const selectedSkills = [...e.target.selectedOptions].map(option => option.value);
    setFormData({ ...formData, skillsRequired: selectedSkills });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Submitting Data:", formData);

    try {
      const response = await fetch("http://localhost:5000/api/jobs", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        alert("Job added successfully!");
        setFormData({
          companyName: "",
          companyLogo: "",
          jobPosition: "",
          monthlySalary: "",
          jobType: "",
          remote_office: "",
          location: "",
          jobDescription: "",
          aboutCompany: "",
          skillsRequired: [],
          additionalInfo: "",
          hiringCount: "",
        });
      } else {
        alert("Failed to add job");
      }
    } catch (error) {
      console.error("Error submitting job:", error);
      alert("Server error, please try again later");
    }
  };

  return (
    <div className="add-job-description">
      <div className="form-container">
        <h2 className="form-heading">Add Job Description</h2>
        <form className="job-form" onSubmit={handleSubmit}>
          <div className="form-row">
            <label className="form-label">Company Name</label>
            <input type="text" name="companyName" placeholder="Enter Company Name" className="form-input" value={formData.companyName} onChange={handleChange} />
          </div>

          <div className="form-row">
            <label className="form-label">Company Logo URL</label>
            <input type="text" name="companyLogo" placeholder="Enter Logo URL" className="form-input" value={formData.companyLogo} onChange={handleChange} />
          </div>

          <div className="form-row">
            <label className="form-label">Job Position</label>
            <input type="text" name="jobPosition" placeholder="Enter Job Position" className="form-input" value={formData.jobPosition} onChange={handleChange} />
          </div>

          <div className="form-row">
            <label className="form-label">Monthly Salary</label>
            <input type="number" name="monthlySalary" placeholder="Enter Salary" className="form-input" value={formData.monthlySalary} onChange={handleChange} />
          </div>

          <div className="form-row">
            <label className="form-label">Job Type</label>
            <select name="jobType" className="form-input" value={formData.jobType} onChange={handleChange}>
              <option value="" disabled>Select Job Type</option>
              <option value="Full-Time">Full-Time</option>
              <option value="Part-Time">Part-Time</option>
              <option value="Internship">Internship</option>
              <option value="Contractual">Contractual</option>
            </select>
          </div>

          <div className="form-row">
            <label className="form-label">Remote/Office</label>
            <select name="remote_office" className="form-input" value={formData.remote_office} onChange={handleChange}>
              <option value="" disabled>Select</option>
              <option value="remote">Remote</option>
              <option value="office">Office</option>
            </select>
          </div>

          <div className="form-row">
            <label className="form-label">Location</label>
            <input type="text" name="location" placeholder="Enter Location" className="form-input" value={formData.location} onChange={handleChange} />
          </div>

          <div className="form-row">
            <label className="form-label">Job Description</label>
            <textarea name="jobDescription" placeholder="Enter Job Description" className="form-textarea" value={formData.jobDescription} onChange={handleChange}></textarea>
          </div>

          <div className="form-row">
            <label className="form-label">About Company</label>
            <textarea name="aboutCompany" placeholder="Type about your company" className="form-textarea" value={formData.aboutCompany} onChange={handleChange}></textarea>
          </div>

          <div className="form-row">
            <label className="form-label">Skills Required</label>
            <select name="skillsRequired" className="form-" multiple value={formData.skillsRequired} onChange={handleSkillsChange}>
              {skillsList.map(skill => (
                <option key={skill} value={skill}>{skill}</option>
              ))}
            </select>
            <p className="selected-skills">Selected: {formData.skillsRequired.join(", ")}</p>
          </div>

          <div className="form-row">
            <label className="form-label">Additional Information</label>
            <input type="text" name="additionalInfo" placeholder="Enter additional information" className="form-input" value={formData.additionalInfo} onChange={handleChange} />
          </div>

          <div className="form-row">
            <label className="form-label">Hiring Count</label>
            <select name="hiringCount" className="form-input" value={formData.hiringCount} onChange={handleChange}>
              <option value="" disabled>Select Hiring Count</option>
              <option value="1-5">1-5</option>
              <option value="6-10">6-10</option>
              <option value="11-20">11-20</option>
              <option value="21-50">21-50</option>
              <option value="51-100">51-100</option>
              <option value="100+">100+</option>
            </select>
          </div>

          <div className="form-buttons">
            <button type="submit" className="btn-add-job">+ Add Job</button>
          </div>
        </form>
      </div>

      <div className="image-container">
        <img src={JobListingimg} alt="Job Illustration" className="job-image" />
      </div>
    </div>
  );
};

export default AddJobDescription;
