import React, { useState } from "react";

const SearchAndSort = ({ onFilter }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortBy, setSortBy] = useState("");

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    onFilter({ searchTerm: e.target.value, sortBy }); // Call the parent function
  };

  const handleSortChange = (e) => {
    setSortBy(e.target.value);
    onFilter({ searchTerm, sortBy: e.target.value }); // Call the parent function
  };

  return (
    <div className="search-sort-container">
      <input
        type="text"
        placeholder="Search jobs..."
        value={searchTerm}
        onChange={handleSearchChange}
        className="search-input"
      />

      <select value={sortBy} onChange={handleSortChange} className="sort-dropdown">
        <option value="">Sort By</option>
        <option value="date">Date Posted</option>
        <option value="salary">Salary</option>
      </select>
    </div>
  );
};

export default SearchAndSort;
