import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import SearchAndSort from "../"; // Import SearchAndSort component
import "./Navbar.css"; // Add your CSS file for styling

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  // Check if the user is logged in when the component mounts
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  // Handle login redirect
  const handleLoginRedirect = () => {
    navigate("/login");
  };

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    navigate("/login");
  };

  // Handle dashboard redirect
  const handleDashboardRedirect = () => {
    navigate("/home");
  };

  // Example functions for filtering and adding jobs
  const handleFilter = (searchTerm, selectedSkills) => {
    console.log("Search Term:", searchTerm);
    console.log("Selected Skills:", selectedSkills);
  };

  const handleAddJob = () => {
    console.log("Redirecting to Add Job page...");
    navigate("/add-job"); // Redirect to the Add Job page
  };

  return (
    <nav className="navbar">
      <h1 className="logo">JobStation</h1>
      <div className="nav-buttons">
        {isLoggedIn ? (
          <>
            <button className="btn" onClick={handleLogout}>Logout</button>
            <button className="btn" onClick={handleDashboardRedirect}>Dashboard</button>
            <img className="profile-icon" src="https://via.placeholder.com/40" alt="Profile" />
          </>
        ) : (
          <>
            <button className="btn" onClick={handleLoginRedirect}>Login</button>
            <button className="btn" onClick={() => navigate("/register")}>Register</button>
          </>
        )}
      </div>

      {/* Pass the isLoggedIn state to the SearchAndSort component */}
      <SearchAndSort
        isLoggedIn={isLoggedIn}
        onFilter={handleFilter}
        onAddJob={handleAddJob}
      />
    </nav>
  );
};

export default Navbar;
