import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Navbar.css";

const Navbar = ({ setIsLoggedIn }) => {
  const [isLoggedIn, setIsLoggedInState] = useState(false);
  const navigate = useNavigate();

  // Check if the user is logged in
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setIsLoggedInState(true);
    }
  }, []);

  const handleLoginRedirect = () => {
    navigate("/login");
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsLoggedInState(false); // Set logged-in state to false
    navigate("/login");
  };

  const handleDashboardRedirect = () => {
    navigate("/home");
  };

  return (
    <nav className="navbar">
      <h1 className="logo">JobStation</h1>
      <div className="nav-buttons">
        {isLoggedIn ? (
          <>
            <button className="btn" onClick={handleLogout}>Logout</button>
            <button className="btn" onClick={handleDashboardRedirect}>Dashboard</button>
            <img className="profile-icon" src="https://via.placeholder.com/40" alt="Profile" />
          </>
        ) : (
          <>
            <button className="btn" onClick={handleLoginRedirect}>Login</button>
            <button className="btn" onClick={() => navigate("/register")}>Register</button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
