import React, { useState } from "react";
import "../styles/Register.css"; // Import external CSS file
import { useNavigate } from "react-router-dom";

const Register = () => {
  const navigate = useNavigate();

  // State for form inputs
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
    mobile: "",  // Added mobile field
    role: "user",
  });

  const [error, setError] = useState("");

  // Handle input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Form validation
  const validateForm = () => {
    const { username, email, password, mobile } = formData;

    if (!username || !email || !password || !mobile) {
      return "All fields are required.";
    }

    if (!/\S+@\S+\.\S+/.test(email)) {
      return "Please enter a valid email address.";
    }

    if (password.length < 6) {
      return "Password must be at least 6 characters.";
    }

    if (!/^\d{10}$/.test(mobile)) {  // Simple validation for 10-digit mobile number
      return "Please enter a valid 10-digit mobile number.";
    }

    return "";
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");  // Reset previous errors
  
    const validationError = validateForm();
    if (validationError) {
      setError(validationError);
      return;
    }
  
    try {
      const response = await fetch("http://localhost:5000/api/users/signup", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
  
      const data = await response.json();
  
      if (response.ok) {
        alert("Account created successfully! You can now log in.");
        navigate("/login");
      } else {
        console.error("Error Response:", data);  // Log the error response
        setError(data.msg || "Something went wrong.");
      }
    } catch (err) {
      console.error("Request Error:", err);  // Log the error details
      setError("Server error. Please try again.");
    }
  };
  

  return (
    <div className="register-page-container">
      <div className="registration-form-container">
        <h2>Create An Account</h2>
        <p>Your Personal Job Finder Is Here</p>

        {error && <p className="error-message">{error}</p>}

        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <input
              type="text"
              name="username"
              placeholder="Username"
              value={formData.username}
              onChange={handleChange}
              required
            />
          </div>

          <div className="input-group">
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="input-group">
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>

          <div className="input-group">
            <input
              type="tel"
              name="mobile"
              placeholder="Mobile"
              value={formData.mobile}
              onChange={handleChange}
              required
            />
          </div>

          <button className="create-account-button" type="submit">
            Create Account
          </button>
        </form>

        <p className="signin-text" onClick={() => navigate("/login")}>
          Already have an account? <a href="#">Sign In</a>
        </p>
      </div>

      <div className="registration-image-container">
        <img src="https://via.placeholder.com/400" alt="Register Visual" />
      </div>
    </div>
  );
};

export default Register;
