import React, { useEffect, useState } from 'react';
import axios from 'axios';
import JobCard from '../JobCard/JobCard';
import SearchAndSort from '../SearchAndSort/SearchAndSort';

const JobListing = () => {
  const [jobs, setJobs] = useState([]);

  const fetchJobs = async (searchTerm = "", skills = []) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/jobs`, {
        params: { searchTerm, skills: skills.join(",") }, // ðŸ”¥ Send search & skills
      });
      setJobs(response.data.jobs);
    } catch (error) {
      console.error("Error fetching jobs:", error);
    }
  };

  useEffect(() => { fetchJobs(); }, []); // Fetch jobs on mount

  return (
    <div>
      <SearchAndSort onFilter={(searchTerm, skills) => fetchJobs(searchTerm, skills)} />
      {jobs.map((job) => <JobCard key={job.id} {...job} />)}
    </div>
  );
};

export default JobListing;
