import React, { useState } from "react";
import "./AddJobDescription.css";
import JobListingimg from "../../../src/assets/job-listing2.jpg";

const skillsList = [
  "HTML", "CSS", "React.js", "JavaScript", "Node.js", "MySQL", "Express.js",
  "REST APIs", "Figma", "Adobe XD", "Wireframing", "Prototyping", "MongoDB",
  "TypeScript", "Redux", "GraphQL", "Bootstrap", "Tailwind CSS", "Material UI",
  "Git", "GitHub", "Docker", "Kubernetes", "AWS", "Firebase", "PostgreSQL",
  "Jest", "Cypress", "CI/CD", "WebSockets", "Next.js", "Flutter", "React Native",
  "Android Development", "iOS Development", "UI/UX Design", "Agile Methodology",
  "Scrum", "JIRA", "Trello", "Web Performance Optimization", "SEO", "Linux",
  "Python", "Django", "Flask", "PHP", "Laravel", "C++", "C#", ".NET", "Java",
  "Spring Boot", "Go", "Rust", "Cybersecurity", "Penetration Testing",
  "Blockchain", "Solidity", "Machine Learning", "Data Science", "TensorFlow",
  "PyTorch", "NLP", "Big Data", "Hadoop", "Power BI", "Tableau"
];

const AddJobDescription = () => {
  const [selectedSkills, setSelectedSkills] = useState([]);
  const [formData, setFormData] = useState({
    companyName: "",
    companyLogo: "",
    jobPosition: "",
    monthlySalary: "",
    jobType: "",
    remote_office: "",
    location: "",
    jobDescription: "",
    aboutCompany: "",
    skillsRequired: [],
    additionalInfo: "",
    hiringCount: "",
  });

  const handleSelectSkill = (e) => {
    const skill = e.target.value;
    if (skill && !selectedSkills.includes(skill)) {
      setSelectedSkills([...selectedSkills, skill]);
    }
  };

  const handleRemoveSkill = (skillToRemove) => {
    setSelectedSkills(selectedSkills.filter(skill => skill !== skillToRemove));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Submitting Data:", formData);

    let token = localStorage.getItem("accessToken");  // Get the stored access token

    try {
        let response = await fetch("http://localhost:5000/api/jobs", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "Authorization": `Bearer ${token}`,  // Attach the access token in Authorization header
            },
            body: JSON.stringify(formData),
        });

        if (response.ok) {
            alert("Job added successfully!");
        } else {
            if (response.status === 401) {  // If the token has expired (401 Unauthorized)
                const refreshToken = localStorage.getItem("refreshToken");  // Get the stored refresh token

                // Make a request to the refresh token endpoint to get a new access token
                const refreshResponse = await fetch("http://localhost:5000/refresh-token", {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify({ refreshToken }),  // Send the refresh token
                });

                if (refreshResponse.ok) {
                    const data = await refreshResponse.json();
                    localStorage.setItem("accessToken", data.accessToken);  // Save the new access token
                    // Retry the original job submission request with the new access token
                    token = data.accessToken;

                    // Retry the job submission request with the new access token
                    const retryResponse = await fetch("http://localhost:5000/api/jobs", {
                        method: "POST",
                        headers: {
                            "Content-Type": "application/json",
                            "Authorization": `Bearer ${token}`,  // Use the new access token
                        },
                        body: JSON.stringify(formData),
                    });

                    if (retryResponse.ok) {
                        alert("Job added successfully!");
                    } else {
                        alert("Failed to add job after retry");
                    }
                } else {
                    alert("Session expired. Please log in again.");
                }
            } else {
                alert("Failed to add job");
            }
        }
    } catch (error) {
        console.error("Error submitting job:", error);
        alert("Server error, please try again later");
    }
};


  return (
    <div className="add-job-description">
      <div className="form-container">
        <h2 className="form-heading">Add Job Description</h2>
        <form className="job-form" onSubmit={handleSubmit}>
          <div className="form-row">
            <label className="form-label">Company Name</label>
            <input
              type="text"
              name="companyName"
              placeholder="Enter Company Name"
              className="form-input"
              value={formData.companyName}
              onChange={handleChange}
            />
          </div>

          <div className="form-row">
            <label className="form-label">Company Logo URL</label>
            <input
              type="text"
              name="companyLogo"
              placeholder="Enter Logo URL"
              className="form-input"
              value={formData.companyLogo}
              onChange={handleChange}
            />
          </div>

          <div className="form-row">
            <label className="form-label">Job Position</label>
            <input
              type="text"
              name="jobPosition"
              placeholder="Enter Job Position"
              className="form-input"
              value={formData.jobPosition}
              onChange={handleChange}
            />
          </div>

          <div className="form-row">
            <label className="form-label">Monthly Salary</label>
            <input
              type="number"
              name="monthlySalary"
              placeholder="Enter Salary"
              className="form-input"
              value={formData.monthlySalary}
              onChange={handleChange}
            />
          </div>

          <div className="form-row">
            <label className="form-label">Job Type</label>
            <select
              name="jobType"
              className="form-input"
              value={formData.jobType}
              onChange={handleChange}
            >
              <option value="" disabled>Select Job Type</option>
              <option value="Full-Time">Full-Time</option>
              <option value="Part-Time">Part-Time</option>
              <option value="Internship">Internship</option>
              <option value="Contractual">Contractual</option>
            </select>
          </div>

          <div className="form-row">
            <label className="form-label">Remote/Office</label>
            <select
              name="remote_office"
              className="form-input"
              value={formData.remote_office}
              onChange={handleChange}
            >
              <option value="" disabled>Select</option>
              <option value="remote">Remote</option>
              <option value="office">Office</option>
            </select>
          </div>

          <div className="form-row">
            <label className="form-label">Location</label>
            <input
              type="text"
              name="location"
              placeholder="Enter Location"
              className="form-input"
              value={formData.location}
              onChange={handleChange}
            />
          </div>

          <div className="form-row">
            <label className="form-label">Job Description</label>
            <textarea
              name="jobDescription"
              placeholder="Enter Job Description"
              className="form-textarea"
              value={formData.jobDescription}
              onChange={handleChange}
            ></textarea>
          </div>

          <div className="form-row">
            <label className="form-label">About Company</label>
            <textarea
              name="aboutCompany"
              placeholder="Type about your company"
              className="form-textarea"
              value={formData.aboutCompany}
              onChange={handleChange}
            ></textarea>
          </div>

          <div className="form-row">
            <label className="form-label">Skills Required</label>
            <div className="form-row-skills">
            <select
              className="form-input"
              onChange={handleSelectSkill}
              defaultValue=""
            >
              <option value="" disabled>Select skills</option>
              {skillsList.map((skill, index) => (
                <option key={index} value={skill}>{skill}</option>
              ))}
            </select>
            {/* Display selected skills below the dropdown */}
            <div className="selected-skills">
              {selectedSkills.map((skill, index) => (
                <div key={index} className="skill-tag">
                  {skill}
                  <button
                    type="button"
                    className="remove-skill"
                    onClick={() => handleRemoveSkill(skill)}
                  >
                    âœ–
                  </button>
                </div>
              ))}
            </div>
            </div>
          </div>
          

          <div className="form-row">
            <label className="form-label">Additional Information</label>
            <input
              type="text"
              name="additionalInfo"
              placeholder="Enter additional information"
              className="form-input"
              value={formData.additionalInfo}
              onChange={handleChange}
            />
          </div>

          <div className="form-row">
            <label className="form-label">Hiring Count</label>
            <select
              name="hiringCount"
              className="form-input"
              value={formData.hiringCount}
              onChange={handleChange}
            >
              <option value="" disabled>Select Hiring Count</option>
              <option value="1-5">1-5</option>
              <option value="6-10">6-10</option>
              <option value="11-20">11-20</option>
              <option value="21-50">21-50</option>
              <option value="51-100">51-100</option>
              <option value="100+">100+</option>
            </select>
          </div>

          <div className="form-buttons">
            <button type="submit" className="btn-add-job">+ Add Job</button>
          </div>
        </form>
      </div>

      <div className="image-container">
        <img src={JobListingimg} alt="Job Illustration" className="job-image" />
      </div>
    </div>
  );
};

export default AddJobDescription;
