import React from 'react';
import { Building2, Users, IndianRupee, Flag, Clock, Briefcase } from 'lucide-react';

interface JobCardProps {
  companyName: string;
  companyLogo: string;
  role: string;
  hiringCount: string;
  salary: string;
  country: string;
  workType: string;
  jobType: string;
  skills: string[];
}

const JobCard: React.FC<JobCardProps> = ({
  companyName,
  companyLogo,
  role,
  hiringCount,
  salary,
  country,
  workType,
  jobType,
  skills,
}) => {
  return (
    <div className="border-l-4 border-red-500 bg-white rounded-lg shadow-sm p-6">
      <div className="flex flex-col gap-4">
        {/* Company and Role */}
        <div className="flex items-start gap-4">
          <img 
            src={companyLogo} 
            alt={companyName}
            className="w-12 h-12 rounded-lg"
          />
          <div>
            <h2 className="text-2xl font-semibold text-gray-900">{role}</h2>
            <h3 className="text-lg text-gray-600">{companyName}</h3>
          </div>
        </div>

        {/* Job Details */}
        <div className="flex flex-wrap gap-6">
          <div className="flex items-center gap-2 text-gray-600">
            <Users className="w-5 h-5" />
            <span>{hiringCount}</span>
          </div>
          <div className="flex items-center gap-2 text-gray-600">
            <IndianRupee className="w-5 h-5" />
            <span>{salary}</span>
          </div>
          <div className="flex items-center gap-2 text-gray-600">
            <Flag className="w-5 h-5" />
            <span>{country}</span>
          </div>
        </div>

        {/* Work Type and Job Type */}
        <div className="flex gap-6">
          <div className="flex items-center gap-2 text-gray-600">
            <Briefcase className="w-5 h-5" />
            <span>{workType}</span>
          </div>
          <div className="flex items-center gap-2 text-gray-600">
            <Clock className="w-5 h-5" />
            <span>{jobType}</span>
          </div>
        </div>

        {/* Skills and Button */}
        <div className="flex items-center justify-between mt-2">
          <div className="flex flex-wrap gap-2">
            {skills.map((skill, index) => (
              <span
                key={index}
                className="px-3 py-1 text-sm bg-pink-50 text-pink-700 rounded-md"
              >
                {skill}
              </span>
            ))}
          </div>
          <button className="px-6 py-2 bg-red-500 text-white rounded-md hover:bg-red-600 transition-colors">
            View details
          </button>
        </div>
      </div>
    </div>
  );
};

export default JobCard;
