import React, { useEffect, useState } from 'react';
import JobCard from '../JobCard/JobCard'; // Import JobCard component
import axios from 'axios'; // Import axios to make API calls

const JobListing = () => {
  const [jobs, setJobs] = useState([]); // State to store jobs
  const [loading, setLoading] = useState(true); // State to manage loading state
  const [error, setError] = useState(null); // State to store any errors

  useEffect(() => {
    // Fetch job listings from backend when the component mounts
    const fetchJobs = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/jobs'); // Make GET request to fetch jobs
        setJobs(response.data); // Store the fetched job data in state
      } catch (err) {
        console.error("Error fetching jobs:", err);
        setError('Failed to load job listings. Please try again later.');
      } finally {
        setLoading(false); // Set loading to false after fetching is done
      }
    };

    fetchJobs(); // Call the fetch function
  }, []); // Empty dependency array ensures this runs only once when the component mounts

  // Handle different loading/error states
  if (loading) {
    return <p>Loading job listings...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
      {jobs.length > 0 ? (
        // Map over the jobs array and display each job using the JobCard component
        jobs.map((job) => (
          <JobCard
            key={job.id} // Ensure job has a unique ID
            id={job.id} // Pass the job ID for navigation
            companyName={job.companyName}
            companyLogo={job.companyLogo} // Added companyLogo field
            jobPosition={job.jobPosition}
            hiringCount={job.hiringCount} // Assuming you have hiringCount in the response
            monthlySalary={job.monthlySalary} // Corrected field to monthlySalary
            jobType={job.jobType}
            workType={job.remote_office === 'remote' ? 'Remote' : 'On-Site'} // Check remote_office field for job type
            location={job.location} // Using location instead of country
            skillsRequired={job.skillsRequired} // Skills list
          />
        ))
      ) : (
        // If there are no jobs, display a message
        <p>No jobs available.</p>
      )}
    </div>
  );
};

export default JobListing;
