import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const JobDetails = () => {
  const { id } = useParams();  // Get the job id from the URL
  const [jobDetails, setJobDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!id) {
      setError("Job ID is missing");
      setLoading(false);
      return;
    }

    // Fetch job details based on the ID from the URL
    axios
      .get(`/api/jobs/${id}`)
      .then((response) => {
        setJobDetails(response.data);
        setLoading(false);
      })
      .catch((err) => {
        setError(`Error fetching job details: ${err.message}`);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Job Details</h1>
      {jobDetails ? (
        <div>
          <h2>{jobDetails.jobPosition}</h2>
          <p>{jobDetails.companyName}</p>
          {/* Display other job details here */}
        </div>
      ) : (
        <div>No job details available</div>
      )}
    </div>
  );
};

export default JobDetails;
