import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { useState, useEffect } from 'react';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import Navbar from './components/Navbar/';  // Import Navbar component
import SearchAndSort from './components/SearchAndSort/SearchAndSort';  // Import SearchAndSort component

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    // Check if the user is logged in when the app starts (to persist login status)
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            setIsLoggedIn(true);
        }
    }, []);

    const handleFilter = (searchTerm, selectedSkills) => {
        console.log("Search Term:", searchTerm);
        console.log("Selected Skills:", selectedSkills);
    };

    const handleAddJob = () => {
        console.log("Redirecting to Add Job page...");
    };

    return (
        <Router>
            <ToastContainer />  {/* âœ… Moved outside Routes */}
            <Navbar setIsLoggedIn={setIsLoggedIn} isLoggedIn={isLoggedIn} />
            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/login" element={<Login />} />
                <Route path="/register" element={<Register />} />
            </Routes>

            {/* Passing isLoggedIn state to SearchAndSort */}
            <SearchAndSort
                isLoggedIn={isLoggedIn}
                onFilter={handleFilter}
                onAddJob={handleAddJob}
            />
        </Router>
    );
}

export default App;
