import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Clock, MapPin, IndianRupee, Calendar, Building2 } from 'lucide-react';
// import './JobDetails.css';

const SkillBadge = ({ skill }) => (
  <span className="skill-badge">{skill}</span>
);

function JobDetails() {
  const { id } = useParams(); // Get the job ID from the URL params
  const [job, setJob] = useState(null);
  const [loading, setLoading] = useState(true);

  // Fetch job data when the component is mounted
  useEffect(() => {
    axios.get(`http://localhost:5000/api/jobs/${id}`) // Ensure your backend API is correct
      .then(response => {
        setJob(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error("Error fetching job details:", error);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (!job) return <div>Job not found</div>;

  return (
    <div className="job-details-wrapper">
      <div className="job-details-container">
        <div className="job-header">
          <div className="job-meta">
            <Clock className="icon" />
            <span>Posted {new Date(job.createdAt).toLocaleDateString()}</span> {/* Format the created date */}
            <span className="separator">•</span>
            <span>{job.jobType}</span>
          </div>
          <h1 className="job-title">{job.jobPosition}</h1>
          <div className="job-location">
            <MapPin className="icon" />
            <span>{job.location}</span>
          </div>
        </div>

        <div className="job-info">
          <div className="job-salary">
            <IndianRupee className="icon" />
            <div>
              <p className="label">Monthly Salary</p>
              <p className="value">₹{job.monthlySalary}</p>
            </div>
          </div>

          <div className="job-duration">
            <Calendar className="icon" />
            <div>
              <p className="label">Duration</p>
              <p className="value">6 Months</p> {/* Update as per actual data */}
            </div>
          </div>
        </div>

        <div className="company-info">
          <h2>About the company</h2>
          <p>{job.aboutCompany}</p>
        </div>

        <div className="job-description">
          <h2>About the job/internship</h2>
          <p>{job.jobDescription}</p>
          <h3>Responsibilities:</h3>
          <ul>
            <li>Work on the development of theme customization</li>
            <li>Implement system integrations</li>
            <li>Contribute to the development of HTML5/CSS/JavaScript</li>
            <li>Optimize for mobile-friendly websites</li>
          </ul>
        </div>

        <div className="skills-required">
          <h2>Skills Required</h2>
          <div className="skills">
            {job.skillsRequired.split(',').map((skill, index) => (
              <SkillBadge key={index} skill={skill} />
            ))}
          </div>
        </div>

        <div className="additional-info">
          <h2>Additional Information</h2>
          <p>{job.additionalInfo}</p>
        </div>
      </div>
    </div>
  );
}

export default JobDetails;
