import React, { useState } from "react";
import "./AddJobDescription.css";
import JobListingimg from "../../../src/assets/job-listing2.jpg";

const skillsList = [
  "HTML", "CSS", "React.js", "JavaScript", "Node.js", "MySQL", "Express.js",
  "MongoDB", "TypeScript", "Redux", "GraphQL", "Bootstrap", "Tailwind CSS",
  "Material UI", "Git", "GitHub", "Docker", "AWS", "Firebase", "PostgreSQL",
  "Python", "Django", "Flask", "PHP", "Laravel", "C++", "C#", "Java", "Spring Boot"
];

const AddJobDescription = () => {
  const [formData, setFormData] = useState({
    companyName: "",
    companyLogo: "",
    jobPosition: "",
    monthlySalary: "",
    jobType: "",
    remote_office: "",
    location: "",
    jobDescription: "",
    aboutCompany: "",
    skillsRequired: [], // Stores selected skills as an array
    additionalInfo: "",
    hiringCount: "",
  });

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle multiple skills selection
  const handleSkillsChange = (e) => {
    const selectedSkills = [...e.target.selectedOptions].map(option => option.value);
    setFormData({ ...formData, skillsRequired: selectedSkills });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Submitting Data:", formData);

    try {
      const response = await fetch("http://localhost:5000/api/jobs", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        alert("Job added successfully!");
        setFormData({
          companyName: "",
          companyLogo: "",
          jobPosition: "",
          monthlySalary: "",
          jobType: "",
          remote_office: "",
          location: "",
          jobDescription: "",
          aboutCompany: "",
          skillsRequired: [],
          additionalInfo: "",
          hiringCount: "",
        });
      } else {
        alert("Failed to add job");
      }
    } catch (error) {
      console.error("Error submitting job:", error);
      alert("Server error, please try again later");
    }
  };

  return (
    <div className="add-job-description">
      <div className="form-container">
        <h2 className="form-heading">Add Job Description</h2>
        <form className="job-form" onSubmit={handleSubmit}>
          <div className="form-row">
            <label className="form-label">Company Name</label>
            <input type="text" name="companyName" placeholder="Enter Company Name" className="form-input" value={formData.companyName} onChange={handleChange} />
          </div>

          <div className="form-row">
            <label className="form-label">Job Position</label>
            <input type="text" name="jobPosition" placeholder="Enter Job Position" className="form-input" value={formData.jobPosition} onChange={handleChange} />
          </div>

          <div className="form-row">
            <label className="form-label">Skills Required</label>
            <select name="skillsRequired" className="form-input" multiple value={formData.skillsRequired} onChange={handleSkillsChange}>
              {skillsList.map(skill => (
                <option key={skill} value={skill}>{skill}</option>
              ))}
            </select>
            <p className="selected-skills">Selected: {formData.skillsRequired.join(", ")}</p>
          </div>

          <div className="form-buttons">
            <button type="submit" className="btn-add-job">+ Add Job</button>
          </div>
        </form>
      </div>

      <div className="image-container">
        <img src={JobListingimg} alt="Job Illustration" className="job-image" />
      </div>
    </div>
  );
};

export default AddJobDescription;
