import React from "react";
import { useNavigate } from "react-router-dom";
import "./Navbar.css";
import ProfileIcon from "../../../src/assets/profile-icon.png";

const Navbar = ({ isLoggedIn, setIsLoggedIn }) => {
  const navigate = useNavigate();

  // Handle login redirect
  const handleLoginRedirect = () => {
    navigate("/login");
  };

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsLoggedIn(false); // Update state to reflect logged out status
    navigate("/login"); // Redirect to login page
  };

  // Handle dashboard redirect
  const handleDashboardRedirect = () => {
    navigate("/home");
  };

  return (
    <nav className="jobstation-navbar">
      <h1 className="jobstation-logo">JobStation</h1>
      <div className="jobstation-nav-buttons">
        {isLoggedIn ? (
          <>
            <button
              className="jobstation-logout-dashboard-btn"
              onClick={handleLogout}
            >
              Logout
            </button>
            <button
              className="jobstation-logout-dashboard-btn"
              onClick={handleDashboardRedirect}
            >
              Dashboard
            </button>
            <img
              className="jobstation-profile-icon"
              src={ProfileIcon}
              alt="Profile"
            />
          </>
        ) : (
          <>
            <button
              className="jobstation-login-btn"
              onClick={handleLoginRedirect}
            >
              Login
            </button>
            <button
              className="jobstation-register-btn"
              onClick={() => navigate("/register")}
            >
              Register
            </button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
