import React, { useState } from "react";
import "./SearchAndSort.css";

const skillsOptions = ["HTML", "CSS", "SQLite", "Python", "JavaScript", "Node Js", "React Js", "Express Js"];

const SearchAndSort = ({ isLoggedIn, onFilter, onAddJob }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedSkills, setSelectedSkills] = useState([]);

  // Handle search input change
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Handle skill selection
  const handleSkillChange = (e) => {
    const selectedSkill = e.target.value;
    if (selectedSkill && !selectedSkills.includes(selectedSkill)) {
      setSelectedSkills([...selectedSkills, selectedSkill]);
    }
    e.target.value = "";
  };

  // Remove a skill
  const removeSkill = (skillIndex) => {
    setSelectedSkills(selectedSkills.filter((_, index) => index !== skillIndex));
  };

  // Clear filters
  const clearFilters = () => {
    setSearchTerm("");
    setSelectedSkills([]);
  };

  return (
    <div className="search-filter-container">
      <input
        type="text"
        placeholder="Search jobs by title, company, or keywords..."
        value={searchTerm}
        onChange={handleSearchChange}
        className="search-input"
      />

      <div className="filter-row">
        <select className="skills-dropdown" onChange={handleSkillChange}>
          <option value="">Select Skills</option>
          {skillsOptions.map((skill, index) => (
            <option key={index} value={skill}>
              {skill}
            </option>
          ))}
        </select>

        <div className="selected-skills">
          {selectedSkills.map((skill, index) => (
            <div key={index} className="skill-chip-container">
              <span className="skill-chip">
                {skill}
                <span className="remove-skill" onClick={() => removeSkill(index)}>âœ–</span>
              </span>
            </div>
          ))}
        </div>

        {/* Right side buttons for Apply Filter / Clear or Add Job */}
        <div className="buttons-container">
          {isLoggedIn ? (
            <button className="add-job-btn" onClick={onAddJob}>+ Add Job</button>
          ) : (
            <>
              <button className="apply-filter-btn" onClick={() => onFilter(searchTerm, selectedSkills)}>Apply Filter</button>
              <span className="clear-filters" onClick={clearFilters}>Clear</span>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default SearchAndSort;
