import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate instead of useHistory
import "./Navbar.css";

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate(); // useNavigate hook for navigation

  const handleAuth = () => {
    setIsLoggedIn(!isLoggedIn);
  };

  const handleLoginRedirect = () => {
    navigate("/login"); // Redirect to login page using navigate
  };

  return (
    <nav className="navbar">
      <h1 className="logo">JobStation</h1>
      <div className="nav-buttons">
        {isLoggedIn ? (
          <>
            <button className="btn" onClick={handleAuth}>Logout</button>
            <button className="btn">Dashboard</button>
            <img className="profile-icon" src="https://via.placeholder.com/40" alt="Profile" />
          </>
        ) : (
          <>
            <button className="btn" onClick={handleLoginRedirect}>Login</button>
            <button className="btn">Register</button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
