import React, { useState, useEffect } from 'react';
import { Routes, Route, useLocation } from 'react-router-dom';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import Navbar from './components/Navbar/Navbar';
import SearchAndSort from './components/SearchAndSort/SearchAndSort';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import JobListing from './components/JobListing/JobListing';
import JobDetails from './components/JobDetails/JobDetails';
import AddJobDetails from '../src/components/AddJobDescription/AddJobDescription'; 

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const location = useLocation(); 

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, []);

  const hideNavbarOnPages = ["/login", "/register", "/add-job"];
  const showNavbar = !hideNavbarOnPages.includes(location.pathname) || location.pathname.startsWith('/job/');

  return (
    <>
      <ToastContainer />

      {showNavbar && <Navbar isLoggedIn={isLoggedIn} setIsLoggedIn={setIsLoggedIn} />}

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/add-job" element={<AddJobDetails />} />
        <Route path="/job/:id" element={<JobDetails />} /> {/* Job Details Route */}
      </Routes>

      {showNavbar && (
        <>
          <SearchAndSort isLoggedIn={isLoggedIn} />
          <JobListing />
        </>
      )}
    </>
  );
}

export default App;
