import React, { useState } from "react";
import "./SearchAndSort.css";

const skillsOptions = ["HTML", "CSS", "SQLite", "Python", "JavaScript", "Node Js", "React Js", "Express Js"];

const SearchAndSort = ({ onFilter }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedSkills, setSelectedSkills] = useState([]);

  // Handles search input change
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Handles skill selection
  const handleSkillChange = (e) => {
    const selectedSkill = e.target.value;
    if (selectedSkill && !selectedSkills.includes(selectedSkill)) {
      setSelectedSkills([...selectedSkills, selectedSkill]);
    }
  };

  // Removes a skill from selection
  const removeSkill = (skill) => {
    setSelectedSkills(selectedSkills.filter((s) => s !== skill));
  };

  // Clears all selected filters
  const clearFilters = () => {
    setSearchTerm("");
    setSelectedSkills([]);
  };

  return (
    <div className="search-filter-container">
      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search jobs by title, company, or keywords..."
        value={searchTerm}
        onChange={handleSearchChange}
        className="search-input"
      />

      {/* Skills Dropdown */}
      <select className="skills-dropdown" onChange={handleSkillChange}>
        <option value="">Select Skills</option>
        {skillsOptions.map((skill, index) => (
          <option key={index} value={skill}>
            {skill}
          </option>
        ))}
      </select>

      {/* Selected Skills Display */}
      <div className="selected-skills">
        {selectedSkills.map((skill, index) => (
          <span key={index} className="skill-chip">
            {skill} <span className="remove-skill" onClick={() => removeSkill(skill)}>âœ–</span>
          </span>
        ))}
      </div>

      {/* Apply Filter Button */}
      <button className="apply-filter-btn" onClick={() => onFilter(searchTerm, selectedSkills)}>
        Apply Filter
      </button>

      {/* Clear Filters Option */}
      <span className="clear-filters" onClick={clearFilters}>
        Clear
      </span>
    </div>
  );
};

export default SearchAndSort;
